            {{- with .Values.controller.service }}
            - containerPort: {{ .targetPort }}
            {{- range $_, $port := untilStep (.portStart | int) (.portEnd | add1 | int) 1 }}
            - containerPort: {{ $port }}
            {{- end }}
            {{- end }}
          env:
